import { useLocation, useNavigate } from "react-router-dom";
import {
  // goToPokeDetail,
  goToPokeList,
  goToPokePage,
} from "../../routes/coordinator";
import { Button, Container, ContainerImg, MainContainer } from "./headerStyle";

const Header = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const renderHeader = () => {
    if (location.pathname === "/") {
      return (
        <Container>
          <ContainerImg>
            <img
              src="data:image/png;base64,"
              alt="poke"
            />
          </ContainerImg>
          <Button onClick={() => goToPokeList(navigate)}>
            Ver minha pokedex
          </Button>
        </Container>
      );
    } else if (location.pathname === "/PokeList") {
      return (
        <Container>
          <ContainerImg>
            <img
              src="data:image/png;base64,"
              alt="poke"
            />
          </ContainerImg>
          <Button
            onClick={() => {
              goToPokePage(navigate);
            }}
          >
            Voltar para Lista
          </Button>
        </Container>
      );
    } else if (location.pathname === "/PokeDetail") {
      return (
        <Container>
          <ContainerImg>
            <img
              src="data:image/png;base64,"
              alt="poke"
            />
          </ContainerImg>
          <Button
            onClick={() => {
              goToPokePage(navigate);
            }}
          >
            Todos os Pokemons
          </Button>
        </Container>
      );
    }
    return <MainContainer> {renderHeader()}</MainContainer>;
  };

  return renderHeader();
};

export default Header;
